<?phpclass Dashboard {	private $admin_id = null;	private $dashboard_type = null;	public function set_admin_id($admin_id) {		$this->admin_id = $admin_id;	}	public function get_admin_id() {		return $this->admin_id;	}	public function set_dashboard_type($dashboard_type) {		$this->dashboard_type = $dashboard_type;	}	public function get_dashboard_type() {		return $this->dashboard_type;	}	public static function lookup($dbh, $where = 1, $params = array()) {		$sql = "SELECT admin_id, dashboard_type FROM dashboard WHERE $where";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);		$results = $stmt->fetchAll(PDO::FETCH_CLASS, 'dashboard');		return $results;	}	public static function populate($admin_id = null, $dashboard_type = null) {		$item = new dashboard();		$item->set_admin_id($admin_id);		$item->set_dashboard_type($dashboard_type);		return $item;	}	public function write($dbh) {		$params = array(			$this->get_admin_id(),			$this->get_dashboard_type(),		);		if (!$this->admin_id) {			$sql = "INSERT INTO dashboard (admin_id, dashboard_type) 
                    VALUES (?, ?)";		} else {			$sql = "UPDATE dashboard 
                    SET admin_id = ?, dashboard_type = ? 
                    WHERE admin_id = ?";			$params[] = $this->admin_id;		}		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}	public function delete($dbh) {		$params = array($this->admin_id);		$sql = "DELETE FROM dashboard WHERE admin_id = ?";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}}