<?phpclass Permissions {	private $id = null;	private $admin_id = null;	private $permission = null;	public function set_id($id) {		$this->id = $id;	}	public function get_id() {		return $this->id;	}	public function set_admin_id($admin_id) {		$this->admin_id = $admin_id;	}	public function get_admin_id() {		return $this->admin_id;	}	public function set_permission($permission) {		$this->permission = $permission;	}	public function get_permission() {		return $this->permission;	}	public static function lookup($dbh, $where = 1, $params = array()) {		$sql = "SELECT id, admin_id, permission FROM permissions WHERE $where";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);		$results = $stmt->fetchAll(PDO::FETCH_CLASS, 'permissions');		return $results;	}	public static function populate($id = null, $admin_id = null, $permission = null) {		$item = new permissions();		$item->set_id($id);		$item->set_admin_id($admin_id);		$item->set_permission($permission);		return $item;	}	public function write($dbh) {		$params = array(			$this->get_id(),			$this->get_admin_id(),			$this->get_permission(),		);		if (!$this->id) {			$sql = "INSERT INTO permissions (id, admin_id, permission) 
                    VALUES (?, ?, ?)";		} else {			$sql = "UPDATE permissions 
                    SET id = ?, admin_id = ?, permission = ? 
                    WHERE id = ?";			$params[] = $this->id;		}		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}	public function delete($dbh) {		$params = array($this->id);		$sql = "DELETE FROM permissions WHERE id = ?";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}}