<?phpclass Permissions {	private $admin_id = null;	private $permission = null;	public function set_admin_id($admin_id) {		$this->admin_id = $admin_id;	}	public function get_admin_id() {		return $this->admin_id;	}	public function set_permission($permission) {		$this->permission = $permission;	}	public function get_permission() {		return $this->permission;	}	public static function lookup($dbh, $where = 1, $params = array()) {		$sql = "SELECT admin_id, permission FROM permissions WHERE $where";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);		$results = $stmt->fetchAll(PDO::FETCH_CLASS, 'permissions');		return $results;	}	public static function populate($admin_id = null, $permission = null) {		$item = new permissions();		$item->set_admin_id($admin_id);		$item->set_permission($permission);		return $item;	}	public function write($dbh) {		$params = array(			$this->get_admin_id(),			$this->get_permission(),		);		if (!$this->admin_id) {			$sql = "INSERT INTO permissions (admin_id, permission) 
                    VALUES (?, ?)";		} else {			$sql = "UPDATE permissions 
                    SET admin_id = ?, permission = ? 
                    WHERE admin_id = ?";			$params[] = $this->admin_id;		}		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}	public function delete($dbh) {		$params = array($this->admin_id);		$sql = "DELETE FROM permissions WHERE admin_id = ?";		$stmt = $dbh->prepare($sql);		$stmt->execute($params);	}}